from datetime import datetime, timedelta

def _fromutc(self, dt: datetime) -> datetime:
    if dt.tzinfo is None:
        raise ValueError("The datetime object must be timezone-aware")

    # Calculate the UTC offset for the given datetime
    utc_offset = dt.utcoffset()
    if utc_offset is None:
        raise ValueError("The datetime object must have a valid UTC offset")

    # Convert the datetime to UTC by subtracting the UTC offset
    utc_dt = dt - utc_offset

    # Determine if the datetime is in a fold state (ambiguous)
    if hasattr(dt.tzinfo, 'dst'):
        dst_offset = dt.tzinfo.dst(dt)
        if dst_offset is not None and dst_offset != timedelta(0):
            # If the datetime is ambiguous, check if it's the first occurrence
            if hasattr(dt.tzinfo, 'enfold'):
                dt = dt.tzinfo.enfold(dt)

    # Now, convert the UTC datetime to the new timezone by adding the UTC offset
    new_dt = utc_dt + utc_offset

    return new_dt

