            # URI syntax https://www.rfc-editor.org/rfc/rfc3986.html#section-3.1 :
            # scheme = ALPHA *( ALPHA / DIGIT / "+" / "-" / "." )
            if not re.match(r'''[a-z][a-z\d\+\-\.]*:.+''', iid, re.IGNORECASE):
                self.warning("W005", id=iid)
            self.id = iid
