import re

def unquote(name):
    """Remove quote from the given name."""
    assert isinstance(name, str)  # Ensure 'name' is a string

    def unquoted_char(match):
        """For each ';000' pattern, return the corresponding character."""
        try:
            # Convert the numeric part (after ';') to an integer and return the corresponding character
            return chr(int(match.group(0)[1:]))
        except (ValueError, OverflowError):
            # If conversion fails, return the original group as is
            return match.group(0)

    # Replace all occurrences of patterns like `;000` with the unquoted character
    return re.sub(r';\d{3}', unquoted_char, name)

