def ansible_playbook(ir_workspace, ir_plugin, playbook_path, verbose=None, extra_vars=None, ansible_args=None):
    cmd = ["ansible-playbook"]

    # Handle verbosity levels properly
    if verbose is not None:
        if isinstance(verbose, int) and 0 <= verbose <= 5:  # Ansible verbosity levels range from 0 to 5
            cmd.append("-" + "v" * verbose)  # Correct verbatim format for verbosity
        else:
            raise ValueError("Verbose must be an integer between 0 and 5.")

    # Handle extra_vars as a dictionary
    if extra_vars is not None:
        if isinstance(extra_vars, dict):
            # Correct formatting for --extra-vars argument
            serialized_vars = ",".join(f"{key}={value!r}" for key, value in extra_vars.items())
            cmd.append(f"--extra-vars={{ {serialized_vars} }}")
        else:
            raise ValueError("extra_vars must be a dictionary.")

    # Handle ansible_args as a dictionary
    if ansible_args is not None:
        if isinstance(ansible_args, dict):
            for key, value in ansible_args.items():
                cmd.append(f"--{key}={value}")
        else:
            raise ValueError("ansible_args must be a dictionary.")

    cmd.append(playbook_path)  # Append the playbook path as the final command argument

    try:
        subprocess.run(cmd, check=True)
    except subprocess.CalledProcessError as e:
        LOG.error(f"Ansible playbook execution failed: {e}")  # Simplify error message to avoid redundancies
        raise

