from typing import Dict
import json
from xml.etree import ElementTree

def parse_diaspora_webfinger(document: str) -> Dict:
    """
    Parse Diaspora webfinger which is either in JSON format (new) or XRD (old).

    https://diaspora.github.io/diaspora_federation/discovery/webfinger.html
    """
    try:
        # Try parsing as JSON
        data = json.loads(document)
        if 'links' in data and len(data['links']) > 0 and 'href' in data['links'][0]:
            return {
                'hcard_url': data['links'][0]['href']
            }
        else:
            raise ValueError("JSON does not contain required fields.")
    except json.JSONDecodeError:
        # If JSON parsing fails, try parsing as XRD
        try:
            root = ElementTree.fromstring(document)
            # Find the first link element with rel="http://microformats.org/profile/hcard"
            link = root.find(".//{*}Link[@rel='http://microformats.org/profile/hcard']")
            if link is not None and 'href' in link.attrib:
                return {
                    'hcard_url': link.attrib['href']
                }
