def deep_merge_nodes(nodes):
    from ruamel.yaml.nodes import ScalarNode, MappingNode

    def deep_merge_mappings(mapping1, mapping2):
        """Recursively deep merge two MappingNode values."""
        merged = {}
        for k, v in mapping1.value:
            merged[k.value] = v
        for k, v in mapping2.value:
            key_str = k.value
            if key_str in merged and isinstance(merged[key_str], MappingNode) and isinstance(v, MappingNode):
                merged[key_str] = MappingNode(tag=mapping1.tag, value=deep_merge_mappings(merged[key_str], v).items())
            else:
                merged[key_str] = v
        return merged

    result = {}
    for key, value in nodes:
        if isinstance(key, ScalarNode) and isinstance(value, MappingNode):
            key_value = key.value
            if key_value in result and isinstance(result[key_value], MappingNode):
                result[key_value] = MappingNode(
                    tag=value.tag,
                    # Deep merge the mappings
                    value=list(deep_merge_mappings(result[key_value], value).items())
                )
            else:
                result[key_value] = value
        else:
            # Handle non-MappingNode values: overwrite with the last one
            result[key.value] = value

    # Convert the result back into a list of tuples as specified in the docstring
    return [(ScalarNode(tag='tag:yaml.org,2002:str', value=key), value) for key, value in result.items()]

