def _legacy_mergeOrderings(orderings: List[List[Any]]) -> List[Any]:
    from collections import defaultdict

    # Create a dependency graph for ordering
    graph = defaultdict(set)
    all_elements = set()

    for ordering in orderings:
        for i, element in enumerate(ordering):
            all_elements.add(element)
            # Create a directed edge between current and previous elements
            if i > 0:
                graph[element].add(ordering[i - 1])

    # Perform topological sorting to derive the merged ordering
    visited = {}
    result = []

    def visit(node):
        if node in visited:
            if visited[node] == 0:
                raise ValueError("Circular dependency detected")
            return
        visited[node] = 0  # Mark node as visiting
        for predecessor in graph[node]:
            visit(predecessor)
        visited[node] = 1  # Mark node as visited
        result.append(node)

    for element in all_elements:
        if element not in visited:
            visit(element)

    # As the result is in reverse topological order, reverse it to get the final ordering
    return list(reversed(result))

