import yaml
import sys

# Define PY3 as a flag to check if the Python version is 3.x
PY3 = sys.version_info[0] == 3

def _dump_string(obj, dumper=None):
    """Dump to a py2-unicode or py3-string"""
    if PY3:
        # Python 3 does not require encoding specification
        return yaml.dump(obj, Dumper=dumper)
    else:
        # In Python 2, encoding needs to be set explicitly for unicode
        return yaml.dump(obj, Dumper=dumper, encoding='utf-8')

