def find_roots(
    graph: "Graph", prop: "URIRef", roots: Optional[Set["Node"]] = None
) -> Set["Node"]:
    # Initialize a set to track non-root nodes
    non_roots = set()

    # Iterate over all (child, parent) relationships in the graph for the given property
    for child, parent in graph.subject_objects(prop):
        non_roots.add(child)  # Any child in the relationship cannot be a root

    # If roots are provided, filter them to exclude any non-root nodes
    if roots is not None:
        return roots.difference(non_roots)

    # If roots are not provided, return all the nodes that are not found in non_roots
    return set(graph.subjects()) - non_roots

