import yaml
from exceptions import IRExtraVarsException
import dict_utils

@staticmethod
def merge_extra_vars(vars_dict, extra_vars=None):
    """Extend ``vars_dict`` with ``extra-vars``

    :param vars_dict: Dictionary to merge extra-vars into
    :param extra_vars: List of extra-vars
    """
    for extra_var in (extra_vars or []):
        # Handling files specified in extra-vars
        if extra_var.startswith('@'):
            file_path = extra_var[1:]  # Remove '@' to get the file path
            try:
                with open(file_path, 'r', encoding='utf-8') as f_obj:
                    loaded_yml = yaml.safe_load(f_obj)  # Load YAML content from the file
                dict_utils.dict_merge(
                    vars_dict,
                    loaded_yml,
                    conflict_resolver=dict_utils.ConflictResolver.unique_append_list_resolver
                )
            except FileNotFoundError:
                raise IRExtraVarsException(f"File not found: {file_path}")
        else:
            # Handling key-value pairs in extra-vars
            if '=' not in extra_var:
                raise IRExtraVarsException(f"Invalid extra-var format: {extra_var}")

            key, value = extra_var.split('=', 1)
            # Handle value specified with '@' indicating a file
            if value.startswith('@'):
                file_path = value[1:]  # Remove '@' to get the file path
                try:
                    with open(file_path, 'r', encoding='utf-8') as f_obj:
                        loaded_yml = yaml.safe_load(f_obj)  # Load YAML content from the file
                    tmp_dict = {}
                    dict_utils.dict_insert(tmp_dict, *key.split('.'), value=loaded_yml)
                    dict_utils.dict_merge(
                        vars_dict,
                        tmp_dict,
                        conflict_resolver=dict_utils.ConflictResolver.unique_append_list_resolver
                    )
                except FileNotFoundError:
                    raise IRExtraVarsException(f"File not found: {file_path}")
            else:
                # Handle direct key-value assignment
                dict_utils.dict_insert(vars_dict, *key.split('.'), value=value)

    return vars_dict

