def _include_groups(self, parser_dict):
    """
    Resolves the include dict directive in the spec files.

    This method processes the `parser_dict` to resolve any "include" directives.
    It updates `parser_dict` in place, replacing any "include" groups with the respective
    values obtained from the dictionary itself. If errors occur (e.g., missing keys 
    or recursive includes), a `SpecParserException` is raised for proper error handling.

    Args:
        parser_dict (dict): The parser dictionary to be processed for include directives.

    Returns:
        None: The function modifies `parser_dict` in place.
    """
    # Helper function to resolve includes recursively
    def resolve_include(key, seen_keys=None):
        # Track seen keys to prevent infinite recursion
        if seen_keys is None:
            seen_keys = set()
        if key in seen_keys:
            raise SpecParserException(f"Circular include detected for key: {key}")

        seen_keys.add(key)

        # Get the value associated with the key
        include_value = parser_dict.get(key)
        if include_value is None:
            raise SpecParserException(f"Key '{key}' not found in parser_dict during include resolution.")

        if isinstance(include_value, list):
            # Process each element in the list
            resolved_values = []
            for item in include_value:
                if isinstance(item, str) and item.startswith("include:"):
                    # Extract the key to be included and resolve it
                    include_key = item.split("include:", 1)[1]
                    resolved_values.extend(resolve_include(include_key, deepcopy(seen_keys)))
                else:
                    resolved_values.append(item)
            return resolved_values
        elif isinstance(include_value, str) and include_value.startswith("include:"):
            # Handle nested string-based include resolution
            include_key = include_value.split("include:", 1)[1]
            return resolve_include(include_key, deepcopy(seen_keys))
        else:
            # Return non-list, non-include objects directly
            return [include_value]

    # Iterate through parser_dict keys to resolve includes
    for key, value in deepcopy(parser_dict).items():
        if isinstance(value, str) and value.startswith("include:"):
            # Replace include directive with resolved content
            include_key = value.split("include:", 1)[1]
            parser_dict[key] = resolve_include(include_key)
        elif isinstance(value, list):
            # Process each element in the list to resolve includes
            updated_list = []
            for item in value:
                if isinstance(item, str) and item.startswith("include:"):
                    include_key = item.split("include:", 1)[1]
                    updated_list.extend(resolve_include(include_key))
                else:
                    updated_list.append(item)
            parser_dict[key] = updated_list

