import logging

def get_nested_custom_and_control_args(self, args):
    """
    Split input arguments into control, nested, and custom arguments.

    Controls arguments: control the IR behavior. These arguments
        will not be put into the spec yml file.
    Nested arguments: are used by the Ansible playbooks and will be put
        into the spec yml file.
    Custom arguments: Custom ansible variables to be used instead of the
        normal nested usage.

    :param args: the collected list of args.
    :return: (dict, dict): flat dicts (control_args, nested_args)
    """
    control_args = {}
    nested_args = {}
    seen_args = set()  # Track arguments to check for duplicates

    for arg in args:
        if arg in seen_args:
            logging.warning(f"Duplicate argument detected: {arg}")
            continue
        seen_args.add(arg)

        if arg.startswith("--"):
            # Arguments starting with "--" are control arguments
            arg = arg[2:]
            if "=" in arg:
                key, value = arg.split("=", 1)
                control_args[key.strip()] = value.strip()
            else:
                control_args[arg.strip()] = True
        elif arg.startswith("-"):
            # Arguments starting with "-" are also treated as control arguments
            arg = arg[1:]
            if "=" in arg:
                key, value = arg.split("=", 1)
                control_args[key.strip()] = value.strip()
            else:
                control_args[arg.strip()] = True
        else:
            # Non-prefixed arguments are considered nested arguments
            if "=" in arg:
                key, value = arg.split("=", 1)
                nested_args[key.strip()] = value.strip()
            else:
                nested_args[arg.strip()] = True

    # Validate and log warnings for improperly formatted arguments
    for arg in seen_args:
        if " " in arg or not arg:
            logging.warning(f"Improperly formatted argument detected: {arg}")

    return control_args, nested_args

