import subprocess
import logging

# Assuming LOG is a defined logger instance from the Oracle Context
LOG = logging.getLogger(__name__)

def ansible_playbook(ir_workspace, ir_plugin, playbook_path, verbose=None, extra_vars=None, ansible_args=None):
    """
    Wraps the 'ansible-playbook' CLI.

    :param ir_workspace: An Infrared Workspace object represents the active workspace
    :param ir_plugin: An InfraredPlugin object of the current plugin
    :param playbook_path: the playbook to invoke
    :param verbose: Ansible verbosity level
    :param extra_vars: dict. Passed to Ansible as extra-vars
    :param ansible_args: dict of ansible-playbook arguments to plumb down directly to Ansible.
    """
    cmd = ["ansible-playbook"]

    # Validate and append verbosity level
    if verbose is not None:
        if verbose in ("v", "vv", "vvv", "vvvv"):
            cmd.append(f"-{verbose}")
        else:
            LOG.error(f"Invalid verbosity level: {verbose}. Accepted values are 'v', 'vv', 'vvv', or 'vvvv'.")
            raise ValueError("Invalid verbosity level. Use 'v', 'vv', 'vvv', or 'vvvv'.")

    # Validate and append extra variables
    if extra_vars is not None:
        if isinstance(extra_vars, dict):
            formatted_extra_vars = ",".join(f"{key}={value}" for key, value in extra_vars.items())
            cmd.append(f"--extra-vars={formatted_extra_vars}")
        else:
            LOG.error("extra_vars must be a dictionary.")
            raise TypeError("extra_vars must be a dictionary.")

    # Validate and append ansible args
    if ansible_args is not None:
        if isinstance(ansible_args, dict):
            for key, value in ansible_args.items():
                if isinstance(key, str) and isinstance(value, str):
                    cmd.append(f"--{key}={value}")
                else:
                    LOG.error(f"Invalid key-value pair in ansible_args: {key}={value}. Both should be strings.")
                    raise TypeError("Keys and values in ansible_args must be strings.")
        else:
            LOG.error("ansible_args must be a dictionary.")
            raise TypeError("ansible_args must be a dictionary.")

    cmd.append(playbook_path)

    # Run the command with error handling
    try:
        subprocess.run(cmd, check=True)
    except subprocess.CalledProcessError as e:
        LOG.error(f"Ansible playbook execution failed with error code {e.returncode}. Error: {e}")
        raise RuntimeError(f"Ansible playbook execution failed: {e}") from e
    except FileNotFoundError as e:
        LOG.error(f"Command not found: {cmd[0]}. Ensure that Ansible is installed and in the system's PATH.")
