def determineMetaclass(bases, explicit_mc=None):
    """
    Determine metaclass from 1+ bases and optional explicit __metaclass__.

    If `explicit_mc` is provided, it is returned if valid. Otherwise, the metaclass
    is determined by inspecting the `__class__` attributes of the bases or their
    `type`, ensuring compatibility among the bases.
    Raises a TypeError if no valid metaclass can be determined or if the bases 
    are incompatible.
    """
    # If an explicit metaclass is provided, use it
    if explicit_mc is not None:
        return explicit_mc

    # Ensure bases are valid (not empty and properly iterable)
    if not bases or len(bases) == 0:
        raise TypeError("Cannot determine metaclass without bases.")

    # Helper function to find metaclass for a single base
    def get_base_metaclass(base):
        return getattr(base, '__class__', type(base))

    # Collect unique metaclasses from bases
    metaclasses = set(get_base_metaclass(base) for base in bases)

    # If there is only one metaclass, use it
    if len(metaclasses) == 1:
        return metaclasses.pop()

    # Check for metaclass compatibility
    # Choose a metaclass that is parent to others if possible
    for candidate in metaclasses:
        if all(issubclass(mt, candidate) for mt in metaclasses):
            return candidate

    # If no single metaclass is compatible, raise an error
    raise TypeError("Incompatible metaclasses found in bases: {}".format(metaclasses))

