def determineMetaclass(bases, explicit_mc=None):
    """
    Determine metaclass from 1+ bases and optional explicit __metaclass__
    """
    # If an explicit metaclass is provided, use it
    if explicit_mc is not None:
        return explicit_mc

    # If no bases are provided, default to 'type'
    if not bases:
        return type

    # Helper function to find the most derived metaclass
    def most_derived_metaclass(metaclasses):
        """Find the most derived metaclass from a list of metaclasses."""
        winner = metaclasses[0]
        for meta in metaclasses[1:]:
            if issubclass(meta, winner):
                winner = meta
            elif not issubclass(winner, meta):
                raise TypeError("Incompatible metaclasses: {} and {}".format(winner, meta))
        return winner

    # Collect metaclasses from the bases
    metaclasses = []
    for base in bases:
        if hasattr(base, '__class__') and isinstance(base, type):
            metaclasses.append(base.__class__)
        elif isinstance(base, type):
            metaclasses.append(base)
        else:
            raise TypeError("Base {} is not a class or type".format(base))

    # Determine the most derived metaclass
    return most_derived_metaclass(metaclasses)

