def _include_groups(self, parser_dict):
    """
    Resolves the include dict directive in the spec files.
    """
    try:
        # Create a deepcopy of the parser_dict to avoid unintended side effects
        parser_dict_copy = deepcopy(parser_dict)

        # Iterate through the parser_dict to find and process 'include' directives
        for key, value in parser_dict_copy.items():
            if isinstance(value, dict) and 'include' in value:
                include_group = value.get('include')

                # Check if the include_group exists in the parser_dict
                if include_group not in parser_dict_copy:
                    raise SpecParserException(f"Included group '{include_group}' not found in parser_dict.")

                # Merge the included group's values into the current group
                included_values = parser_dict_copy.get(include_group)
                for included_key, included_value in included_values.items():
                    # Avoid overwriting existing keys
                    if included_key not in value:
                        value[included_key] = included_value

        # Return the updated parser_dict
        return parser_dict_copy

    except SpecParserException as e:
        # Handle specific parsing exceptions
        raise e

    except Exception as e:
        # Handle any other unexpected exceptions
        raise SpecParserException(f"An error occurred while resolving include groups: {str(e)}")

