    import yaml

    def is_condition_met(condition, args):
        """
        Safely evaluate the condition using the arguments provided.
        The condition is expected to be a YAML-safe string that can be parsed into a dictionary.
        """
        try:
            # Parse the condition as a YAML-safe string
            parsed_condition = yaml.safe_load(condition)
            if not isinstance(parsed_condition, dict):
                return False

            # Ensure all keys in the condition are satisfied in args
            return all(args.get(key) == value for key, value in parsed_condition.items())
