def pop_u16(self):
    """ Remove the last two bytes of data, returning them as a big-endian
    16-bit unsigned integer.
    """
    if self.used >= 2:
        value = 0x100 * self.data[self.used - 2] + self.data[self.used - 1]
        self.used -= 2
        return value
    else:
        return -1

