            existing_value_node = result[key]
            # Only merge if both values are MappingNodes.
            if isinstance(existing_value_node, MappingNode) and isinstance(value_node, MappingNode):
                merged_mapping = {}
                # Convert the MappingNode's value (which is a list of tuples) into a mergeable dict.
                for sub_key_node, sub_value_node in existing_value_node.value:
                    merged_mapping[sub_key_node.value] = sub_value_node
                for sub_key_node, sub_value_node in value_node.value:
                    if (
                        sub_key_node.value in merged_mapping
                        and isinstance(merged_mapping[sub_key_node.value], MappingNode)
                        and isinstance(sub_value_node, MappingNode)
                    ):
                        # Recursively deep merge conflicting keys in the mapped values.
                        merged_mapping[sub_key_node.value] = MappingNode(
                            tag=sub_value_node.tag,
                            value=[
