                                        for k, v in [
                                            (sub_key_node.value, merged_mapping[sub_key_node.value]),
                                            (sub_key_node.value, sub_value_node),
                                        ]
                                    ]
                                ).items()
                            ],
                        )
                    else:
                        # Otherwise, overwrite by default.
                        merged_mapping[sub_key_node.value] = sub_value_node

