        include_dict = parser_dict["include"]
        for group_name, group_dict in include_dict.items():
            # Ensure group_dict is a dictionary
            if not isinstance(group_dict, dict):
                raise ValueError(f"The group '{group_name}' must be a dictionary.")

            if group_name in self.groups:
