def _convert_non_cli_args(self, parser_name, values_dict):
    """Casts arguments to correct types by modifying values_dict param.

    By default all the values are strings.

    :param parser_name: The command name, e.g. main, virsh, ospd, etc
    :param values_dict: The dict of with arguments
    """
    for opt_name, opt_value in values_dict.items():
        file_option_spec = self.spec_helper.get_option_spec(
            parser_name, opt_name)

        # Check if 'type' is specified and appropriately cast the value
        if file_option_spec.get('type', None) == 'float':
            try:
                values_dict[opt_name] = float(opt_value)
            except ValueError:
                raise ValueError(f"Cannot convert {opt_value} to float for option {opt_name}.")
        elif file_option_spec.get('type', None) == 'int':
            try:
                values_dict[opt_name] = int(opt_value)
            except ValueError:
                raise ValueError(f"Cannot convert {opt_value} to int for option {opt_name}.")

        # Check if 'action' is specified for 'count' and cast the value to int
        elif file_option_spec.get('action', None) == 'count':
            try:
                values_dict[opt_name] = int(opt_value)
            except ValueError:
                raise ValueError(f"Cannot convert {opt_value} to int for count action on option {opt_name}.")
    return values_dict

