def deep_merge_nodes(nodes):
    """
    Given a nested borgmatic configuration data structure as a list of tuples in the form of:
        (
            ruamel.yaml.nodes.ScalarNode as a key,
            ruamel.yaml.nodes.MappingNode or other Node as a value,
        ),
    ... deep merge any node values corresponding to duplicate keys and return the result. If
    there are colliding keys with non-MappingNode values (e.g., integers or strings), the last
    of the values wins.
    """
    replaced_nodes = {}
    for a_key, a_value in nodes:
        for b_key, b_value in nodes:
            if (a_key, a_value) in replaced_nodes or (b_key, b_value) in replaced_nodes:
                continue
            if (
                a_key.tag == b_key.tag
                and a_key.value == b_key.value
                and a_value != b_value
            ):
                replaced_nodes[a_key, a_value] = DELETED_NODE
                if isinstance(b_value, ruamel.yaml.nodes.MappingNode):
                    # Perform deep merging for MappingNode
                    merged_value = []
                    a_value_dict = {key.value: value for key, value in a_value.value}
                    b_value_dict = {key.value: value for key, value in b_value.value}

                    for key in set(a_value_dict.keys()).union(b_value_dict.keys()):
                        if key in a_value_dict and key in b_value_dict:
                            merged_value.append(
                                (
                                    ruamel.yaml.nodes.ScalarNode(
                                        tag="tag:yaml.org,2002:str", value=key
                                    ),
                                    b_value_dict[key]
                                    if not isinstance(
                                        a_value_dict[key], ruamel.yaml.nodes.MappingNode
                                    )
                                    else ruamel.yaml.nodes.MappingNode(
                                        tag=b_value_dict[key].tag,
                                        value=deep_merge_nodes(
                                            [(a_value_dict[key], b_value_dict[key])]
                                        ),
                                        start_mark=b_value_dict[key].start_mark,
                                        end_mark=b_value_dict[key].end_mark,
                                    ),
                                )
                            )
                        elif key in a_value_dict:
                            merged_value.append(
                                (
                                    ruamel.yaml.nodes.ScalarNode(
                                        tag="tag:yaml.org,2002:str", value=key
                                    ),
                                    a_value_dict[key],
                                )
                            )
                        else:
                            merged_value.append(
                                (
                                    ruamel.yaml.nodes.ScalarNode(
                                        tag="tag:yaml.org,2002:str", value=key
                                    ),
                                    b_value_dict[key],
                                )
                            )

                    replaced_nodes[b_key, b_value] = (
                        b_key,
                        ruamel.yaml.nodes.MappingNode(
                            tag=b_value.tag,
                            value=merged_value,
                            start_mark=b_value.start_mark,
                            end_mark=b_value.end_mark,
                            flow_style=b_value.flow_style,
                            comment=b_value.comment,
                            anchor=b_value.anchor,
