    required_args = []
    for option_spec in options_spec:
        if 'required_when' in option_spec:
            condition = option_spec['required_when']
            try:
                # Safely evaluate the condition using the `ast.literal_eval` for security
                parsed_condition = ast.literal_eval(condition)
                if isinstance(parsed_condition, bool) and parsed_condition:
