from typing import Optional, Set
from rdflib import Graph, URIRef

def find_roots(
    graph: "Graph", prop: "URIRef", roots: Optional[Set["Node"]] = None
) -> Set["Node"]:
    non_roots = set()

    # Collect all nodes that appear as objects of the given property
    for _, obj in graph.subject_objects(prop):
        non_roots.add(obj)

    if roots is None:
        roots = set()

    # Collect all subjects that are not in non_roots (i.e., roots of the hierarchy)
    for subj, _ in graph.subject_objects(prop):
        if subj not in non_roots:
            roots.add(subj)

    return roots

