def check_digests_present_and_used(self, manifest_files: List[str], digests_used: List[str]) -> None:
    """
    Check all digests in manifest that are needed are present and used.
    """
    digests_present = set()
    for manifest_file in manifest_files:
        with open(manifest_file, 'r') as f:
            for line in f:
                parts = line.strip().split()
                if len(parts) == 2:
                    digests_present.add(parts[0])

    digests_needed = set(digests_used)

    missing_digests = sorted(list(digests_needed.difference(digests_present)))
    if missing_digests:
        self.error(f"Missing digests: {', '.join(missing_digests)}")

    unused_digests = sorted(list(digests_present.difference(digests_needed)))
    if unused_digests:
        self.error(f"Unused digests: {', '.join(unused_digests)}")

