from zipfile import ZipFile, is_zipfile
from collections import defaultdict

def _explore_zipfile(zip_path: str) -> dict:
    def _group_files_by_xml_filename(file_list: List[str]) -> List[List[str]]:
        groups = defaultdict(list)
        for filename in file_list:
            basename = filename.rsplit('.', 1)[0]  # Split at last dot
            groups[basename].append(filename)
        return dict(groups)

    if not is_zipfile(zip_path):
        raise ValueError(f"{zip_path} is not a valid zip file.")

    with ZipFile(zip_path, 'r') as zip_ref:
        namelist = zip_ref.namelist()
        return _group_files_by_xml_filename(namelist)

