def popitem(self):
    """Remove and return the `(key, value)` pair first inserted."""
    try:
        key = next(iter(self.__order))
    except StopIteration:
        raise KeyError('%s is empty' % type(self).__name__) from None
    else:
        return (key, self.remove(key))  # Changed 'pop' to 'remove'

