        RE_CMD_LEX = r'"((?:\\\\["\\\\]|[^"])*)"|\'([^\']*)\'|(\\\\.)|(&&?|\\|\\|?|\d?>|[<])|([^\\s\'"\\\\&|<>]+)|(\\s+)|(.)'
    else:  # Windows/CMD
        RE_CMD_LEX = r'"((?:""|\\\\["\\\\]|[^"])*)"?()|(\\\\\\\\(?=\\\\*")|\\\\")|(&&?|\\|\\|?|\d?>|[<])|([^\\s"&|<>]+)|(\\s+)|(.)'

    args = []
    accu = None
    for qs, qss, esc, pipe, word, white, fail in re.findall(RE_CMD_LEX, s):
        if word:
            pass
        elif esc:
            word = esc[1]
        elif white or pipe:
            if accu is not None:
                args.append(accu)
            if pipe:
                args.append(pipe)
            accu = None
            continue
        elif fail:
            raise ValueError('invalid or incomplete shell string')
        elif qs:
            word = qs.replace('\\"', '"').replace('\\\\', '\\')
            if platform == 0:
                word = word.replace('""', '"')
        elif qss:
            word = qss
        else:
            pass  # This ensures no unexpected behavior if none of the conditions are met.

        if word is not None:
            accu = (accu or '') + word

    if accu is not None:
        args.append(accu)
    return args

