def find_roots(
    graph: "Graph", prop: "URIRef", roots: Optional[Set["Node"]] = None
) -> Set["Node"]:
    if roots is None:
        roots = set()
    non_roots = set()
    for s, p, o in graph.subject_objects(prop):
        if p == prop:
            non_roots.add(s)
    for node in graph.subjects():
        if node not in non_roots:
            roots.add(node)
    return roots

