from datetime import datetime, timezone

def hydrate_time(nanoseconds, tz=None):
    """
    Hydrator for `Time` and `LocalTime` values.

    :param nanoseconds:
    :param tz:
    :return: Time
    """
    seconds, nanoseconds_remainder = divmod(nanoseconds, 1000000000)
    milliseconds = nanoseconds_remainder // 1000000

    if tz is None:
        dt = datetime.utcfromtimestamp(seconds)
        return Time(dt.hour, dt.minute, dt.second, milliseconds * 1000, tzinfo=timezone.utc)
    elif isinstance(tz, int):
        tz = FixedOffset(tz // 60, '+%02d:%02d' % divmod(tz, 60))
        dt = datetime.utcfromtimestamp(seconds)
        dt = dt.replace(tzinfo=timezone.utc)
        return dt.astimezone(tz).timetz()
    else:
        return Time(0, 0, 0).localize(tz, nanoseconds)


