from fs import open_fs
from fs.errors import ResourceNotFound

def find_namastes(fs):
    """
    yield (path, content) of namaste files
    """
    for path, info in fs.walk.info(namespaces=['details']):
        if info.is_file and info.name.startswith('0=ocfl_1.'):
            with fs.open(path, 'r') as f:
                content = f.read()
            yield path, content

def find_path_type(path: str) -> str:
    """
    Return a string indicating the type of thing at the given path

    Return values:
        'root' - looks like an OCFL Storage Root
        'object' - looks like an OCFL Object
        'file' - a file, might be an inventory
        other string explains error description

    Looks only at "0=*" Namaste files to determine the directory type.
    """
    try:
        with open_fs(path) as fs:
            namastes = list(find_namastes(fs))
            if len(namastes) == 0:
                try:
                    fs.getinfo('/', namespaces=['details'])
                    return 'file'  # It exists, but no namastes. Might be a file?
                except ResourceNotFound:
                    return 'not found'
            elif len(namastes) == 1:
                m = re.match(r'(.*/)?0=ocfl_1\.0', namastes[0][0])
                if m:
                    if len(m.group(1) or '') == 0:
                        return 'root'
                    else:
                        return 'object'
            return f'found {len(namastes)} namastes'
    except Exception as e:
        return str(e)

