def identify_request(request: RequestType, request_body: str) -> bool:
    """
    Try to identify whether this is a Diaspora request.

    Try first public message. Then private message. The check if this is a legacy payload.
    """

    if request == RequestType.JSON:
        try:
            request_body = decode_if_bytes(request_body)
            data = loads(request_body)

            if DIASPORA_PUBLIC_MESSAGE_JSON_KEY in data:
                return True
            elif DIASPORA_PRIVATE_MESSAGE_JSON_KEY in data:
                return True
            elif DIASPORA_LEGACY_PAYLOAD_JSON_KEY in data:
                return True
        except Exception: # json parsing could fail. Catch the generic exception
            return False


    elif request == RequestType.XML:
        try:
            request_body = encode_if_text(request_body)
            root = fromstring(request_body)
            if root.tag == DIASPORA_MAGIC_ENV_TAG:
                return True
        except Exception:  # xml parsing could fail. Catch the generic exception
            return False

    return False

