def _convert_non_cli_args(parser_name: str, values_dict: dict):
    def cast_to_bool(value):
        return value.lower() in ['true', '1', 'yes']

    def cast_to_int(value):
        try:
            return int(value)
        except ValueError:
            return value

    def cast_to_float(value):
        try:
            return float(value)
        except ValueError:
            return value

    def cast_to_list(value):
        return value.split(',')

    def cast_to_dict(value):
        pairs = value.split(',')
        dictionary = {}
        for pair in pairs:
            try:
                key, val = pair.split(':', 1)
                dictionary[key] = val
            except ValueError:
                pass  # Ignore if a pair doesn't have a ':'
        return dictionary

    for key, value in values_dict.items():
        if isinstance(value, str):
            if value.lower() in ['true', 'false']:
                values_dict[key] = cast_to_bool(value)
            elif value.isdigit():
                values_dict[key] = cast_to_int(value)
            elif '.' in value and all(char.isdigit() for char in value.replace('.', '', 1)):
                values_dict[key] = cast_to_float(value)
            elif ',' in value and ':' in value:
                values_dict[key] = cast_to_dict(value)
            elif ',' in value:
                values_dict[key] = cast_to_list(value)

