from concurrent.futures import ThreadPoolExecutor


def to_output(cmd: str) -> str:
    return cmd


def _get_platform_max_length() -> int:
    return 2000000 if platform.system() == "Windows" else 2097152


def normalize_cmd(cmd: Tuple[str, ...]) -> Tuple[str, ...]:
    return tuple(x for arg in cmd for x in shlex.split(arg))


def find_executable(cmd: str) -> str:
    return cmd


def cmd_fn(
    cmd: Tuple[str, ...], varargs: Tuple[str, ...], color: bool = False, **kwargs: Any
) -> str:
    cmd = normalize_cmd(cmd)
    executable = find_executable(cmd[0])
    if not executable:
        raise OSError("No such file or directory: {}".format(cmd[0]))
    if len(cmd) + len(varargs) <= 1:
        return to_output(" ".join(cmd + varargs))

    full_cmd = [executable] + list(cmd[1:]) + list(varargs)
    try:
        return to_output(subprocess.check_output(full_cmd, **kwargs).decode())

    except subprocess.CalledProcessError as e:
        raise RuntimeError("Command failed: {}".format(e)) from e

def _thread_mapper(
    fn: Callable[[Tuple[str, ...], Tuple[str, ...], bool, Any], str],
    cmd: Tuple[str, ...],
    varargs_partitions: Sequence[Tuple[str, ...]],
    color: bool,
    kwargs: Any,
) -> str:
    results = []
    for varargs in varargs_partitions:
        results.append(fn(cmd, varargs, color, **kwargs))
    return "\n".join(results)

def thread_map(fn, cmd, varargs_partitions, color, kwargs, target_concurrency):
    with ThreadPoolExecutor(max_workers=target_concurrency) as executor:
        futures = [executor.submit(_thread_mapper, fn, cmd, varargs, color, kwargs) for varargs in varargs_partitions]
        results = [future.result() for future in futures]
    return "\n".join(results)


def partition(cmd: Tuple[str, ...], varargs: Sequence[str], max_length: int) -> Sequence[Tuple[str, ...]]:
    partitions = []
    current_partition = []
    current_length = len(" ".join(cmd)) + 1

    for arg in varargs:
        arg_length = len(arg) + 1
        if current_length + arg_length > max_length:
            partitions.append(tuple(current_partition))
            current_partition = []
            current_length = len(" ".join(cmd)) + 1

        current_partition.append(arg)
        current_length += arg_length

    partitions.append(tuple(current_partition))
    return partitions


def xargs(
        cmd: Tuple[str, ...],
        varargs: Sequence[str],
        *,
        color: bool = False,
        target_concurrency: int = 1,
        _max_length: int = _get_platform_max_length(),
        **kwargs
) -> None:
    """
    Simplified Implementation of Xargs in Linux
    """

    max_length = min(_max_length, _get_platform_max_length())
    varargs_partitions = partition(cmd, varargs, max_length)
    target_concurrency = max(1, min(target_concurrency, len(varargs_partitions)))

    print(thread_map(cmd_fn, cmd, varargs_partitions, color, kwargs, target_concurrency))

