def get_pattern(pattern, strip=False, anchor=True):
    """
    Converts the given pattern to a compiled regex pattern.

    Args:
        pattern: The pattern to compile. Can be a string or a pre-compiled regex pattern (re.Pattern object).
        strip: If True and pattern is a string, leading/trailing whitespace will be removed before compilation. Defaults to False.
        anchor: If True, adds an end-of-string anchor ($) to the pattern, ensuring it matches only at the end of the string. 
                If False, the pattern can match substrings. Defaults to True.

    Returns:
        A compiled regex pattern (re.Pattern object).

    Raises:
        TypeError: If pattern is neither a string nor a pre-compiled regex pattern.
    """
    if type(pattern) == re.Pattern:
        return pattern

    if type(pattern) == str:
        if strip:
            pattern = pattern.strip()
        if anchor:
            return re.compile(pattern + "$")
        else:
            return re.compile(pattern)

