        file_option_spec = self.spec_helper.get_option_spec(parser_name,
            opt_name)
        opt_type = file_option_spec.get('type')
        if opt_type:
            if isinstance(opt_type, list):
                opt_type = opt_type[0]
            try:
                if opt_type == 'int':
                    values_dict[opt_name] = int(opt_value)
                elif opt_type == 'float':
                    values_dict[opt_name] = float(opt_value)
                elif opt_type == 'bool':
                    values_dict[opt_name] = bool(opt_value)
            except (ValueError, TypeError):
                # Handle cases where conversion fails
                pass  # Or raise an exception, log a warning, etc.

    return values_dict

