from datetime import datetime, timezone

def hydrate_time(nanoseconds, tz=None):
    """
    Hydrator for `Time` and `LocalTime` values.

    :param nanoseconds:
    :param tz:
    :return: Time
    """
    seconds, nanoseconds = divmod(nanoseconds, 1000000000)
    minutes, seconds = divmod(seconds, 60)
    hours, minutes = divmod(minutes, 60)

    if tz:
        if isinstance(tz, int):
            tz = FixedOffset(tz // 60)
        try:
            dt = datetime(1970, 1, 1, hours, minutes, seconds, nanoseconds, tzinfo=timezone.utc)
            localized_dt = dt.replace(tzinfo=timezone.utc).astimezone(tz)
            return Time(localized_dt.hour, localized_dt.minute, localized_dt.second, localized_dt.microsecond * 1000, tzinfo=tz)
        except Exception:  # OverflowError or TypeError
            return None
    else:
        return Time(hours, minutes, seconds, nanoseconds)


class Time(datetime):
    def __new__(cls, *args, **kwargs):
        if len(args) == 1 and isinstance(args[0], str) and args[0].endswith('Z'):
            # Assuming ISO format string ending with 'Z' for UTC
            dt = datetime.fromisoformat(args[0][:-1] + '+00:00')
            return super().__new__(cls, dt.year, dt.month, dt.day, dt.hour, dt.minute, dt.second, dt.microsecond, dt.tzinfo)
        else:
            return super().__new__(cls, *args, **kwargs)


class FixedOffset(timezone):
    """
    Fixed offset in minutes east from UTC.
    """

    def __init__(self, offset, name=None):
        self.__offset = offset
        self.__name = name

    def utcoffset(self, dt):
        return timedelta(minutes=self.__offset)

    def tzname(self, dt):
        return self.__name

    def dst(self, dt):
        return timedelta(0)

