import os
from typing import List, Dict, Tuple
from xml.etree import ElementTree as ET

def xml_files_list(folder: str) -> List[str]:
    """List all xml files in folder"""
    return [
        os.path.join(folder, f)
        for f in os.listdir(folder)
        if os.path.isfile(os.path.join(folder, f)) and f.lower().endswith(".xml")
    ]


def files_list(folder: str) -> List[str]:
    """List all files in folder"""
    return [
        os.path.join(folder, f)
        for f in os.listdir(folder)
        if os.path.isfile(os.path.join(folder, f))
    ]

def is_folder(path: str) -> bool:
    return os.path.isdir(path)


def _group_files_by_xml_filename(
    folder: str,
) -> Dict[str, List[str]]:
    """Groups files by their XML basename.

    Parameters
    ----------
    folder : str
        Folder of the package

    Returns
    -------
    dict
        A dictionary where keys are XML basenames and values are lists of filepaths.
    """
    groups: Dict[str, List[str]] = {}
    xml_files = xml_files_list(folder)

    for xml_file in xml_files:
        basename = os.path.splitext(os.path.basename(xml_file))[0]
        groups[basename] = [
            f for f in files_list(folder) if os.path.splitext(os.path.basename(f))[0] == basename
        ]
    return groups



def _explore_folder(folder: str) -> dict:
    """
    Get packages' data from folder

    Groups files by their XML basename and returns data in dict format.

    Parameters
    ----------
    folder : str
        Folder of the package
    Returns
    -------
    dict
    """
    return _group_files_by_xml_filename(folder)

