def absorb(self, args):
    """
    Given an `args` sequence of expressions, return a new list of expression
    applying absorption and negative absorption.

    See https://en.wikipedia.org/wiki/Absorption_law

    Absorption::

        A & (A | B) = A, A | (A & B) = A

    Negative absorption::

        A & (~A | B) = A & B, A | (~A & B) = A | B
    """

    from sympy import Symbol, Or, And, Not

    def apply_absorption(expr):
        if isinstance(expr, And):
            a, b = sorted(expr.args, key=len)
            if isinstance(b, Or) and a in b.args:
                return a
        elif isinstance(expr, Or):
            a, b = sorted(expr.args, key=len)
            if isinstance(b, And) and a in b.args:
                return a
        return expr


    def apply_negative_absorption(expr):

        if isinstance(expr, And):
            if len(expr.args) == 2:
                a, b = expr.args
                if isinstance(b, Or) and len(b.args) == 2:
                    x, y = b.args
                    if isinstance(x, Not) and x.args[0] == a:
                        return And(a, y)

        elif isinstance(expr, Or):
            if len(expr.args) == 2:
                a, b = expr.args
                if isinstance(b, And) and len(b.args) == 2:
                    x, y = b.args
                    if isinstance(x, Not) and x.args[0] == a:
                        return Or(a, y)
        return expr

    result = []
    for expression in args:
        expression = apply_absorption(expression)
        expression = apply_negative_absorption(expression)
        result.append(expression)

    return result

