import re
from fsspec import open_fs
from pathlib import Path

def find_namastes(path: str):
    """
    Return a list of 0=... namaste files found under the given path.

    If path is a URL to a directory, appropriate fsspec filesystem will be used.
    If it is a local path, Pathlib will be used.
    """
    fs_root, fs_path = path.split("://", 1) if "://" in path else ("", path)
    if fs_root:
        fs = open_fs(fs_root + "://")
        paths = [
            fs_path + "/" + p
            for p in fs.glob(fs_path + "/**/*")
            if fs.getinfo(p).is_file()
        ]
    else:
        paths = [str(p) for p in Path(fs_path).glob("**/*") if p.is_file()]

    namastes = []
    for path in paths:
        try:
            match = re.match(r"^0=ocfl_([0-9.]+)$", open_fs(path).open().read().strip())
            if match:
                namastes.append((match.group(1), path))
        except Exception as e:
            # If we can't read the file, ignore the error
            pass
    return namastes



def find_path_type(path: str) -> str:
    """Return a string indicating the type of thing at the given path.

    Return values:
        'root' - looks like an OCFL Storage Root
        'object' - looks like an OCFL Object
        'file' - a file, might be an inventory
        other string explains error description

    Looks only at "0=*" Namaste files to determine the directory type.
    """
    namastes = find_namastes(path)
    if len(namastes) > 1:
        return "root"
    elif len(namastes) == 1:
        version, namaste_path = namastes[0]
        if namaste_path.split("/")[-2] == version:
            return "object"
        else:
            return f"unexpected object version: {version}"
    else:
        try:
            fs_root, fs_path = path.split("://", 1) if "://" in path else ("", path)
            if fs_root:
                fs = open_fs(fs_root + "://")
                if fs.getinfo(fs_path).is_file():
                    return 'file'
                else:
                    return "empty dir"
            else:
                if Path(fs_path).is_file():
                    return "file"
                else:
                    return "empty dir"
        except:
            return "empty dir"

