def bash_completion():
    """Return a bash completion script for the borgmatic command. Produce this by introspecting
    borgmatic's command-line argument parsers.
    """

    all_flags = []
    all_options = []

    parsers = make_parsers()
    for name, parser in parsers.items():
        flags = parser_flags(parser)
        all_flags.extend(flags)

        for action in parser._actions:
            for option_string in action.option_strings:
                if action.choices is not None:
                    # Handle "choice" options by just including them in the choices list.
                    all_options.append((option_string, tuple(action.choices)))

                elif action.type == Str:
                    # String options, and basically everything else, just gets the option added to
                    # the list of possible completions.
                    all_options.append((option_string, ()))

    lines = []
    lines.append('_borgmatic() {')
    lines.append('  local cur')
    lines.append('  cur="${COMP_WORDS[COMP_CWORD]}"')
    lines.append('  COMPREPLY=( $(compgen -W "' + " ".join(quote(f) for f in all_flags) + '" -- ${cur}) )')

    for option, choices in all_options:
        if choices:
            lines.append(
                '  COMPREPLY+=( $(compgen -W "'
                + " ".join(quote(c) for c in choices)
                + '" -- ${cur}) )'
            )
        else:
            lines.append('  COMPREPLY+=( $(compgen -f -- ${cur}) )')

    lines.append('}')
    lines.append('complete -o filenames -F _borgmatic borgmatic')

    return "\n".join(lines)


def parser_flags(parser):
    flags = []
    for action in parser._actions:
        for option_string in action.option_strings:
            # Only include flags in the flags list. Exclude options with arguments.
            if len(option_string) > 1 and option_string.startswith("-"):
                flags.append(option_string)

    # Sort flags by length (reversing to put --flags after -f) and then alpha. This isn't
    # strictly needed, just makes output nicer/easier to read.
    flags.sort(key=lambda item: (-len(item), item))

    return flags

