import yaml
import os
from yaml import SafeLoader

class IRValidatorException(Exception):
    pass

def validate_from_file(cls, yaml_file=None):
    """Loads & validates that a YAML file has all required fields

    :param yaml_file: Path to YAML file
    :raise IRValidatorException: when mandatory data is missing in file
    :return: Dictionary with data loaded from a YAML file
    """
    if yaml_file is None:
        raise ValueError("yaml_file is required")

    if not os.path.isfile(yaml_file):
        raise IRValidatorException(f"File not found: {yaml_file}")

    data = {}

    with open(yaml_file, 'r') as file:
        try:
            data = yaml.load(file, Loader=SafeLoader)
        except yaml.YAMLError as e:
            raise IRValidatorException(f"Error parsing YAML file: {e}")

    if not isinstance(data, dict):
        raise IRValidatorException("YAML file does not contain a valid dictionary")

    if "mandatory_field1" not in data:
        raise IRValidatorException("mandatory_field1 is missing")

    if "mandatory_field2" not in data:
        raise IRValidatorException("mandatory_field2 is missing")

    if "mandatory_field3" not in data:
        raise IRValidatorException("mandatory_field3 is missing")

    return data

