import os
import yaml

def get_plugin_spec_flatten_dict(plugin_dir):
    """
    Creates a flat dict from the plugin spec

    :param plugin_dir: A path to the plugin's dir
    :return: A flatten dictionary contains the plugin's properties
    """
    plugin_spec = {}
    for root, dirs, files in os.walk(plugin_dir):
        for file in files:
            if file.endswith(".yaml"):
                file_path = os.path.join(root, file)
                with open(file_path, "r") as f:
                    data = yaml.safe_load(f)
                    if data:  # Check if data is not None or empty
                        def flatten(d, parent_key='', sep='_'):
                            items = []
                            for k, v in d.items():
                                new_key = parent_key + sep + k if parent_key else k
                                if isinstance(v, dict):
                                    items.extend(flatten(v, new_key, sep=sep).items())
                                elif isinstance(v, list):
                                    for i, item in enumerate(v):
                                        if isinstance(item, dict):
                                            items.extend(flatten(item, new_key + f"_{i}", sep=sep).items())
                                        else:
                                            items.append((new_key + f"_{i}", item))

                                else:
                                    items.append((new_key, v))
                            return dict(items)

                        plugin_spec.update(flatten(data))
    return plugin_spec

