def hydrate_time(nanoseconds, tz=None):
    """
    Hydrator for `Time` and `LocalTime` values.

    :param nanoseconds:
    :param tz:
    :return: Time
    """
    seconds, nanoseconds = divmod(nanoseconds, 1000000000)
    minutes, seconds = divmod(seconds, 60)
    hours, minutes = divmod(minutes, 60)

    if tz:
        from dateutil.tz import tzoffset
        try:
            offset = int(tz)
            tz = tzoffset(None, offset)
        except ValueError:
            # Assume string, and leave as is.  We'll try to make sense of it later.
            pass

        try:
            return Time(hours, minutes, seconds, nanoseconds, tzinfo=tz)
        except:  # noqa
            # The tzinfo was not in a valid format, so try to make sense of it as fixed offset from UTC.
            # This is occasionally useful when receiving time values from clients that are not tz-aware.
            try:
                offset = int(tz)
            except:
                offset = 0
            return Time(hours, minutes, seconds, nanoseconds, tzinfo=FixedOffset(offset))

    else:
        return Time(hours, minutes, seconds, nanoseconds)

