def _fromutc(self, dt: datetime) -> datetime:
    if not isinstance(dt, datetime):
        raise TypeError("Expected a datetime object")

    if dt.tzinfo is None:
        raise ValueError("Expected a timezone-aware datetime")

    # Check for ambiguity and fold
    offset = dt.utcoffset()
    if offset is None:
        raise ValueError("utcoffset returned None")

    dt = dt.replace(tzinfo=None)
    dt = self.tz.fromutc(dt.replace(tzinfo=self.tz))
    return self.enfold(dt)

