def find_path_type(path: str) -> str:
    """Return a string indicating the type of thing at the given path.

    Return values:
        'root' - looks like an OCFL Storage Root
        'object' - looks like an OCFL Object
        'file' - a file, might be an inventory
        other string explains error description

    Looks only at "0=*" Namaste files to determine the directory type.
    """
    from ocfl.namaste import find_namastes, open_fs
    from re import match

    fs = open_fs(path)
    namastes = list(find_namastes(fs, path))
    if len(namastes) == 0:
        try:
            fs.getinfo(path)  # Check it's a file
        except FileNotFoundError:
            return f"directory not found: {path}"
        except IsADirectoryError:
            pass  # Just a dir with no namastes?
        return 'file'

    for n in namastes:
        m = match(r"^0=ocfl_([^\.]+)\.txt$", n.name)
        if m:
            version = m.group(1)
            if version == '1.0':
                if str(n.parent) == path:
                    return 'object'
                else:
                    return 'root'  # In a sub-dir
            else:
                return f"unsupported ocfl version: {version}"
        else:
            return f"unknown namaste file: {n.name}"

    # Should be impossible due to previous return in loop
    return f"unexpected error checking: {path}"

