def parse_filename(filename):
    if '/' not in filename:
        return None

    split_path = filename.split('/')
    if len(split_path) == 2 and '.' not in split_path[0]:
        # env/bin/cmd
        return (split_path[0], f'{split_path[1]}')
    else:  # very deep path
        # a/b/c/d/e
        exe = split_path[-1]
        venv = os.sep.join(split_path[:-1])
        return (venv, exe)


def normexe(orig_cmd):
    if not isinstance(orig_cmd, (tuple, list)):
        orig_cmd = (orig_cmd,)

    filename = orig_cmd[0]

    venv = parse_filename(filename)

    if venv:
        _, exe = venv

        cmd = tuple([os.path.join(*venv), *orig_cmd[1:]])
    elif filename.startswith('python') and filename != 'python':
        # python2, python3 etc. is executable
        cmd = orig_cmd
    else:  # do not do anything if command is a python reserved word
        cmd = orig_cmd

    if cmd[0].startswith('python'):
        cmd = (filename,) + cmd[1:]
    return cmd


def normalize_cmd(cmd: tuple[str, ...]) -> tuple[str, ...]:
    return normexe(cmd)

