def _explore_folder(folder: str) -> dict:
    """
    Get packages' data from folder

    Groups files by their XML basename and returns data in dict format.

    Parameters
    ----------
    folder : str
        Folder of the package
    Returns
    -------
    dict
    """
    from packtools.sps.utils import file_utils

    if not file_utils.is_folder(folder):
        return {}

    files = file_utils.files_list(folder)
    xml_files = file_utils.xml_files_list(files)

    return _group_files_by_xml_filename(xml_files, files)

def _group_files_by_xml_filename(xml_files: list, files: list) -> dict:
    """Groups files by their XML basename."""
    from packtools.sps.utils import file_utils

    grouped_files = {}

    for xml_file in xml_files:
        basename = file_utils.basename(xml_file)
        grouped_files[basename] = {"xml": xml_file}

        for file in files:
            if file_utils.basename(file) == basename and not file.endswith(".xml"):
                ext = file_utils.splitext(file)[1][1:].lower()  # Extract extension and convert to lowercase
                grouped_files[basename][ext] = file
    return grouped_files

