def tzname_in_python2(namefunc):
    @wraps(namefunc)
    def adjust_encoding(*args, **kwargs):
        name = namefunc(*args, **kwargs)
        if isinstance(name, bytes):
            return name
        return name.encode('utf-8')
    return adjust_encoding

